module.exports = function (title, borderColor) {
    var Box = new Object;

    Box.title = title;
    Box.borderColor = borderColor;
    Box.id = boxid;

    /* Default timeout is infinity (= -1). */
    /* timeout should be explicitely set by individual modules */
    Box.timeout = -1;

    Box.setContent = function (content) {
        Box.html = content;
    };

    boxid += 1;

    /* Methods */

    return Box;
};
