/* This module returns a dictionary defination of a word */

exports.invokeID = 'define';

exports.build = function (query) {
    var Box = require('boxmodule'),
        definebox = Box('Definition', query),
        google = require('google');

    google.resultsPerPage = 1;

    google('define: ' + query.text, function(err, next, links) {
        if (err) definebox.errorReport(err);

        for (var i = 0; i < 1; i++) {
            definebox.appendListItem(links[i].title,
                                    links[i].description +
                                    '<br>' +
                                    definebox.linkify(links[i].link));
        }
    });

    return definebox;
};
