exports.invokeID = "google";

exports.shortcut = "C-g";

exports.build = function (query) {
    var Box = require('boxmodule'),
        googlebox = Box('Google Search', query),
        google = require('google');

    google.resultsPerPage = 3;
    var nextCounter = 0;

    google(query.text, function(err, next, links) {
        if (err) googlebox.errorReport(err);
        // console.log(links);
        for (var i = 0; i < links.length; ++i) {

            googlebox.appendListItem(links[i].title,
                                    links[i].description +
                                    "<br>" +
                                    googlebox.linkify(links[i].link));

        }
    });

    return googlebox;
};
